/**
 * n8n Code node
 * - KBU MN069 1~5페이지 파싱
 * - 출력: 각 아이템을 단일 JSON {"공지사항","URL","datetime"}로 직렬화하여 CONTENT 필드에 담아 반환
 * - 다음 노드: Google Sheets → Clear(A1:A) → Append (컬럼: CONTENT 1개)
 */

const BASE_URL = 'https://www.kbu.ac.kr/kor/CMS/Board/Board.do?mCode=MN069';
const MAX_PAGES = 10;

// 정규화 URL 구성 요소
const CANON_BASE   = 'https://www.kbu.ac.kr/kor/CMS/Board/Board.do';
const CANON_MCODE  = 'MN069';
const CANON_PAGE   = '1';
const CANON_MODE   = 'view';
const CANON_MGRSEQ = '38';

// 유틸
const S = (x) => String(x ?? '');
const strip = (html) => S(html).replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();

// n8n httpRequest
async function getPageHtml(page) {
  const url = BASE_URL;
  const qs = { robot: 'Y', page: String(page) };
  const res = await this.helpers.httpRequest({
    method: 'GET',
    url,
    qs,
    headers: { 'User-Agent': 'n8n-vanilla-js-scraper' },
    returnFullResponse: false,
  });
  return res;
}

// board_seq 추출
function extractBoardSeq(href) {
  const m = S(href).match(/[?&]board_seq=(\d+)/);
  return m ? m[1] : '';
}

// 정규화 URL 생성 (URLSearchParams 미사용 버전)
function makeCanonicalUrl(boardSeq) {
  if (!boardSeq) return '';
  const pairs = [
    ['robot', 'Y'],
    ['mCode', CANON_MCODE],
    ['page', CANON_PAGE],
    ['mode', CANON_MODE],
    ['mgr_seq', CANON_MGRSEQ],
    ['board_seq', boardSeq],
  ];
  const qs = pairs.map(([k, v]) => k + '=' + encodeURIComponent(String(v))).join('&');
  return CANON_BASE + '?' + qs;
}

// 테이블형 파싱 → [{NOTICE, URL, DATETIME}]
function parseTable(html) {
  const out = [];
  const tbodyMatch = html.match(
    /<table[^>]*class=["'][^"']*bdListTbl[^"']*["'][\s\S]*?<tbody[^>]*>([\s\S]*?)<\/tbody>/i
  );
  if (!tbodyMatch) return out;
  const tbody = tbodyMatch[1];

  const trRe = /<tr[^>]*>([\s\S]*?)<\/tr>/gi;
  let tr;
  while ((tr = trRe.exec(tbody)) !== null) {
    const tds = [];
    const tdRe = /<td[^>]*>([\s\S]*?)<\/td>/gi;
    let td;
    while ((td = tdRe.exec(tr[1])) !== null) tds.push(td[1]);
    if (tds.length < 5) continue;

    let aCell, dateCell;
    if (tds.length >= 6) { aCell = tds[2]; dateCell = tds[4]; }
    else { aCell = tds[1]; dateCell = tds[3]; }

    const aMatch = aCell.match(/<a[^>]*href=["']([^"']+)["'][^>]*>([\s\S]*?)<\/a>/i);
    if (!aMatch) continue;

    const rawHref = aMatch[1];
    const title   = strip(aMatch[2]);

    const dateText = strip(dateCell).replace(/\./g, '-').trim();
    const dateIso  = /^\d{4}-\d{2}-\d{2}$/.test(dateText) ? dateText : '';

    const boardSeq = extractBoardSeq(rawHref);
    const canonUrl = makeCanonicalUrl(boardSeq);

    out.push({
      NOTICE: title,
      URL: canonUrl || '',
      DATETIME: dateIso,
    });
  }
  return out;
}

async function main() {
  const rows = [];
  for (let page = 1; page <= MAX_PAGES; page++) {
    const html = await getPageHtml.call(this, page);
    if (!html) {
      console.log(`[WARN] 페이지 ${page}: 응답 없음`);
      continue;
    }
    const items = parseTable(html);
    console.log(`[INFO] 페이지 ${page}: ${items.length}건`);
    for (let i = 0; i < items.length; i++) {
      const it = items[i];
      console.log(`  [${page}-${i + 1}] ${it.DATETIME} | ${it.NOTICE} | ${it.URL}`);
      // CONTENT 단일 필드에 JSON 문자열로 넣기 (키: 공지사항/URL/datetime)
      const contentJson = JSON.stringify({
        공지사항: it.NOTICE,
        URL: it.URL,
        datetime: it.DATETIME,
      });
      rows.push({ json: { CONTENT: contentJson } });
    }
  }
  console.log(`[INFO] 총 수집: ${rows.length}건`);
  return rows;
}

return await main();

/**
 * n8n Code node
 * - 학사일정 HTML(여러 달 블록 포함)에서 [이름, 기간] 추출
 * - 출력: CONTENT 단일 컬럼에 "[학사일정, yyyy-MM-dd ~ yyyy-MM-dd]" 문자열
 * - 다음 노드: Google Sheets → Clear(A1:A) → Append (컬럼: CONTENT 1개)
 */

const CAL_URL='https://www.kbu.ac.kr/kor/CMS/ScheduleMgr/YearList.do?mCode=MN083';

// ───────── 유틸 ─────────
const S = (x) => String(x ?? '');
const strip = (html) => S(html).replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
const pad2 = (n) => (n < 10 ? '0' + n : '' + n);
const toISO = (y, m, d) => `${y}-${pad2(m)}-${pad2(d)}`;

// 날짜 문자열 파싱: "MM. DD(요일) ∼ MM. DD(요일)" 또는 "MM. DD(요일)"
function parseDateRange(text, yearFromSection) {
  const t = S(text).replace(/\s+/g, ' ').trim();

  // 범위 (∼ 또는 ~)
  let m = t.match(/(\d{2})\.\s*(\d{2})\s*\([^)]+\)\s*[~∼]\s*(\d{2})\.\s*(\d{2})\s*\([^)]+\)/);
  if (m) {
    const sm = parseInt(m[1], 10), sd = parseInt(m[2], 10);
    const em = parseInt(m[3], 10), ed = parseInt(m[4], 10);
    const ys = yearFromSection;
    const ye = (em < sm) ? (ys + 1) : ys; // 연도 넘어가는 경우(12→01)
    return { start: toISO(ys, sm, sd), end: toISO(ye, em, ed) };
  }

  // 단일 날짜
  m = t.match(/(\d{2})\.\s*(\d{2})\s*\([^)]+\)/);
  if (m) {
    const sm = parseInt(m[1], 10), sd = parseInt(m[2], 10);
    const ys = yearFromSection;
    const iso = toISO(ys, sm, sd);
    return { start: iso, end: iso };
  }

  // 파싱 실패
  return { start: '', end: '' };
}

// HTML 가져오기(없으면 CAL_URL에서 GET)
async function fetchHtmlIfNeeded() {
  const inlineHtml = S(($json && ($json.HTML ?? $json.html)) || '');
  if (inlineHtml) return inlineHtml;
  if (!CAL_URL || CAL_URL.includes('<<')) {
    throw new Error('HTML 입력이 없고 CAL_URL이 설정되지 않았습니다.');
  }
  const res = await this.helpers.httpRequest({
    method: 'GET',
    url: CAL_URL,
    headers: { 'User-Agent': 'n8n-vanilla-js-scraper' },
    returnFullResponse: false,
  });
  return res;
}

// 메인 파서
function parseAcademic(html) {
  const rows = [];

  // 월 블록 단위로 분리
  const parts = html.split(/<div class="sch-monlist-wr">/g).slice(1);

  for (const part of parts) {
    // 섹션 연도 추출: <h4 class="cal-month"><span>2025. 01</span></h4>
    const ym = part.match(/class="cal-month"><span>\s*(\d{4})\.\s*\d{2}\s*<\/span>/);
    const year = ym ? parseInt(ym[1], 10) : new Date().getFullYear();

    // 상세 일정 li 추출
    const liRe = /<li[^>]*class=["']daily-li["'][^>]*>([\s\S]*?)<\/li>/g;
    let li;
    while ((li = liRe.exec(part)) !== null) {
      const seg = li[1];

      const dateMat = seg.match(/class=["']date-core["'][^>]*>([\s\S]*?)<\/div>/);
      const bodyMat = seg.match(/class=["']body-core["'][^>]*>([\s\S]*?)<\/div>/);

      const dateRaw = dateMat ? strip(dateMat[1]) : '';
      const nameRaw = bodyMat ? strip(bodyMat[1]) : '';
      if (!dateRaw || !nameRaw) continue;

      const { start, end } = parseDateRange(dateRaw, year);
      if (!start || !end) continue;

      const content = `[${nameRaw}, ${start} ~ ${end}]`;
      rows.push({ json: { CONTENT: content } });
    }
  }

  return rows;
}

async function main() {
  const html = await fetchHtmlIfNeeded.call(this);
  const items = parseAcademic(html);
  console.log(`[INFO] 학사일정 추출: ${items.length}건`);
  // 로그 일부 샘플
  for (let i = 0; i < Math.min(items.length, 10); i++) {
    console.log(`  ${items[i].json.CONTENT}`);
  }
  return items;
}

return await main();

const FORM_URL = 'https://cute-commonly-meerkat.ngrok-free.app/form/bd6b3857-4d1d-4a32-82b1-08a8c74911a9';

return [
  {
    json: {
      message: `민원 접수를 위해 아래 링크의 폼을 열어 작성해 주세요.\n<a href="${FORM_URL}">폼 열기</a>`
    }
  }
];

/**
 * n8n Code node
 * - 최종 문장을 만들어 지정된 웹훅으로 POST 전송
 * - 기대 입력: $json.selectedTeam (또는 $json.team), $json.departments (선택)
 */

const WEBHOOK_URL = 'https://cute-commonly-meerkat.ngrok-free.app/webhook/61446b74-ef74-4de4-a733-71e35fa65794/chat';

// 1) 팀명 선택 로직
const depts = Array.isArray($json.departments) ? $json.departments : [];
let team = ($json.selectedTeam ?? $json.team ?? depts[0] ?? '총무팀').toString().trim();
if (!team.endsWith('팀')) team += '팀';

// 2) 최종 메시지 생성
const message = `문의해주신 민원은 ${team}으로 전달되었습니다. 감사합니다.`;

// 3) 웹훅으로 전송
let responseBody;
try {
  responseBody = await this.helpers.httpRequest({
    method: 'POST',
    url: WEBHOOK_URL,
    headers: { 'Content-Type': 'application/json' },
    body: { message }, // 서버가 기대하는 키가 다르면 여기서 수정
    json: true,
  });
  console.log('[OK] Sent to webhook:', message);
} catch (err) {
  console.log('[ERROR] Failed to send to webhook:', err?.message || err);
  // 실패하더라도 다음 노드에서 확인할 수 있도록 반환
  return [{ json: { message, error: String(err?.message || err) } }];
}

// 4) 다음 노드로 결과 전달
return [{ json: { message, webhookResponse: responseBody } }];
